module.exports = function(){
	
	var thingsToLog = []
	  , important
	  , mutted = process.argv.find('mutted') ? true : false
	  , quiet  = process.argv.find('quiet') ? true : false
	  , chatty = process.argv.find('chatty') ? true : false

	if (mutted) return

	arguments.each(function(i, argument){
		if ( 'important' === argument ) important = true
		if ( 'important' !== argument ) thingsToLog.push(argument)
	})

	function logThis( things ){
		things.each(function(thing){
			console.log( thing )
		})
	}

	if ( !quiet && !chatty ) quiet = true
	  
	if ( chatty || (quiet && important) ) logThis( thingsToLog )
}